CREATE OR REPLACE VIEW V_AT_A_ENTITY_PARAMS  AS
SELECT
  A.SETUP_ENTITY_PARAM_ID,
  B.ENTITY_PARAM_ID,
  C.ENTITY_ID,
  AT_SEP.OPTION_ID AS AT_OPTION_ID,
  C.ENTITY_NAME,
  C.PARTNER_NAME,
  A.PARAM_NAME,
  A.INPUT_LABEL,
  B.PARAM_VALUE,
  A.AT_UPDATE_PARAM_ID,
  A.AT_GROUP_VALUE_ID,
  AT_SEP.PARAM_NAME    AS AT_PARAM_NAME,
  AT_SOV.VALUE         AS AT_VALUE,
  AT_SOV.DISPLAY_ORDER AS AT_ORDER,
  OV.DISPLAY_ORDER     AS CURRENT_ORDER,
  (
    SELECT
      MAX(Z.DISPLAY_ORDER)
    FROM
      SETUP_OPTION_VALUES Z
    WHERE
      Z.OPTION_ID = A.OPTION_ID
  ) AS MAX_ORDER,
  (
    SELECT
      COUNT(*)
    FROM
      SETUP_ENTITY_PARAMS Y
    WHERE
      Y.AT_GROUP_VALUE_ID = A.AT_GROUP_VALUE_ID
  ) AS MAX_COUNT
FROM
  STAGE_ENTITY_PARAMS B
INNER JOIN SETUP_ENTITY_PARAMS A
ON
  A.PARAM_NAME = B.PARAM_NAME
INNER JOIN STAGE_ENTITY C
ON
  C.ENTITY_ID = B.ENTITY_ID
INNER JOIN SETUP_ENTITY_PARAMS AT_SEP
ON
  AT_SEP.SETUP_ENTITY_PARAM_ID = A.AT_UPDATE_PARAM_ID
INNER JOIN SETUP_OPTION_VALUES AT_SOV
ON
  AT_SOV.OPTION_VALUE_ID = A.AT_GROUP_VALUE_ID
INNER JOIN SETUP_PARAM_OPTION O
ON
  O.OPTION_ID = A.OPTION_ID
INNER JOIN SETUP_OPTION_VALUES OV
ON
  O.OPTION_ID = OV.OPTION_ID
AND OV.VALUE  = B.PARAM_VALUE
WHERE
  A.AT_UPDATE_PARAM_ID  IS NOT NULL
AND A.AT_GROUP_VALUE_ID IS NOT NULL
AND A.AT_FLAG            = 1 
;





CREATE OR REPLACE VIEW V_AT_A_FLOW_PARAMS  AS
