CREATE OR REPLACE VIEW V_AT_CURRENT_STATUS  AS
SELECT
  B.ENTITY_ID,
  B.ENTITY_NAME,
  B.AT_PARAM_NAME,
  B.AT_OPTION_ID,
  B.AT_UPDATE_PARAM_ID,
  B.AT_ORDER
FROM
  V_AT_A_ENTITY_PARAMS B
WHERE
  (
    B.CURRENT_ORDER = B.MAX_ORDER
