CREATE OR REPLACE VIEW V_ENTITY_COMM_OVERRIDE_PARAMS  AS
SELECT
  E.ENTITY_ID,
  ECP.ENTITY_COMM_ID,
  SCP.COMM_PARAM_ID,
  ECP.PARAM_ID,
  O.PARAM_ID AS OVERRIDE_PARAM_ID,
  SCP.COMM_ID,
  DF.FLOW_ID,
  E.PARTNER_NAME,
  EC.COMM_NAME,
  EC.COMM_TYPE,
  EC.PROCESS_NAME,
  EC.PROTOCOL,
  SCP.PARAM_NAME,
  CASE
    WHEN SCP.SENSITIVE_FLAG = 1
    AND
      (
        ECP.PARAM_VALUE IS NULL
      OR ECP.PARAM_VALUE = ''
      )
    THEN NULL
    WHEN SCP.SENSITIVE_FLAG = 1
    AND ECP.PARAM_VALUE    IS NOT NULL
    THEN '-NotReal-'
    ELSE ECP.PARAM_VALUE
  END AS DISPLAY_VALUE,
  ECP.PARAM_VALUE,
  O.PARAM_VALUE AS OVERRIDE_VALUE,
  SCP.INPUT_ORDER,
  SCP.INPUT_LABEL,
  SCP.INPUT_HELP_INFO,
  SCP.INPUT_TYPE,
  SCP.INPUT_VALIDATION,
  SCP.DEFAULT_VALUE,
  SCP.REQUIRED_FLAG,
  SCP.OPTION_ID,
  SCP.OVERRIDE_FLAG,
  SCP.SENSITIVE_FLAG,
  SCP.SETUP_LINK_URI,
  SCP.SETUP_LINK_LABEL,
  P.PARTNER_NAME AS PRODUCER_NAME
FROM
  SETUP_COMM_PARAMS SCP
INNER JOIN STAGE_ENTITY_COMM_PARAMS ECP
ON
  SCP.PARAM_NAME = ECP.PARAM_NAME
INNER JOIN SETUP_COMMS SC
ON
  SC.SETUP_COMM_ID = SCP.COMM_ID
INNER JOIN STAGE_ENTITY_COMM EC
ON
  EC.ENTITY_COMM_ID = ECP.ENTITY_COMM_ID
AND SC.PROCESS_NAME = EC.PROCESS_NAME
AND SC.PROTOCOL     = EC.PROTOCOL
AND EC.COMM_TYPE    = 'CONSUME'
INNER JOIN STAGE_ENTITY E
ON
  E.ENTITY_ID = EC.ENTITY_ID
INNER JOIN STAGE_DATA_FLOW DF
