  A.INPUT_ORDER,
  A.PARAM_NAME,
  A.INPUT_LABEL,
  A.INPUT_TYPE,
  A.INPUT_HELP_INFO,
  A.DEFAULT_VALUE,
  A.INPUT_VALIDATION,
  A.SENSITIVE_FLAG,
  A.PROTECTED_FLAG,
  A.REQUIRED_FLAG,
  A.ON_GRID_FLAG,
  A.OPTION_ID,
  B.ENTITY_PARAM_ID,
  B.ENTITY_ID,
  (
    SELECT COUNT(*)
    FROM STAGE_ENTITY_COMM SEC
    WHERE SEC.ENTITY_ID = B.ENTITY_ID
  ) AS COMM_COUNT,
  (
    SELECT COUNT(*)
    FROM STAGE_DATA_FLOW SDF
    WHERE
      (
        SDF.ENTITY_ID = B.ENTITY_ID
      )
    OR
      (
        SDF.CONSUMER_ENTITY_ID = B.ENTITY_ID
      )
  ) AS FLOW_COUNT,
  CASE
    WHEN A.SENSITIVE_FLAG = 1
    AND
      (
        B.PARAM_VALUE IS NULL
      OR B.PARAM_VALUE = ''
      )
    THEN NULL
    WHEN A.SENSITIVE_FLAG = 1
    AND B.PARAM_VALUE    IS NOT NULL
    THEN '-NotReal-'
    ELSE B.PARAM_VALUE
  END AS DISPLAY_VALUE,
  B.PARAM_VALUE,
  C.ENTITY_NAME,
  C.PARTNER_NAME,
  T.TAB_ID,
  T.TAB_NAME,
  T.ORDER_NUM,
  B.LAST_MOD_USER,
  B.LAST_MOD_DATE,
  A.AT_FLAG,
  A.AT_UPDATE_PARAM_ID
FROM STAGE_ENTITY_PARAMS B
INNER JOIN SETUP_ENTITY_PARAMS A
ON A.PARAM_NAME = B.PARAM_NAME
INNER JOIN STAGE_ENTITY C
ON C.ENTITY_ID = B.ENTITY_ID
INNER JOIN SETUP_ENTITY_PARAM_TABS T
ON T.TAB_ID = A.TAB_ID 
;





