CREATE OR REPLACE VIEW V_ONBOARDING_WORKFLOW  AS
SELECT
  OBW.WORKFLOW_ID,
  OBW.WORKFLOW_NAME,
  OBW.PRODUCER_ID,
  OBW.CONSUMER_ID,
  OBW.PRODUCER_COMM_ID,
  OBW.CONSUMER_COMM_ID,
  OBW.DATA_FLOW_ID,
  P.ENTITY_NAME AS P_NAME,
  C.ENTITY_NAME AS C_NAME,
  PC.COMM_NAME  AS P_COMM_NAME,
  PC.PROTOCOL   AS P_PROTOCOL,
  CC.COMM_NAME  AS C_COMM_NAME,
  CC.PROTOCOL   AS C_PROTOCOL,
  DF.FLOW_NAME,
  (
    SELECT
      COUNT(*)
    FROM
      V_MISSING_REQUIRED_PARAMS MP
    WHERE
      MP.ENTITY_ID = OBW.PRODUCER_ID
    AND MP.TYPE    = 'Partner Parameter'
  ) AS P_MISSING_COUNT,
  (
    SELECT
      COUNT(*)
    FROM
      V_MISSING_REQUIRED_PARAMS MC
    WHERE
      MC.ENTITY_ID = OBW.CONSUMER_ID
    AND MC.TYPE    = 'Partner Parameter'
  ) AS C_MISSING_COUNT,
  (
    SELECT
      COUNT(*)
    FROM
      V_MISSING_REQUIRED_PARAMS MPC
    WHERE
      MPC.TYPE_ID = OBW.PRODUCER_COMM_ID
    AND MPC.TYPE  = 'Communication Parameter'
  ) AS PC_MISSING_COUNT,
  (
    SELECT
      COUNT(*)
    FROM
      V_MISSING_REQUIRED_PARAMS MCC
    WHERE
      MCC.TYPE_ID = OBW.CONSUMER_COMM_ID
    AND MCC.TYPE  = 'Communication Parameter'
  ) AS CC_MISSING_COUNT,
  (
    SELECT
      COUNT(*)
    FROM
      V_MISSING_REQUIRED_PARAMS MDF
    WHERE
      MDF.TYPE_ID = OBW.DATA_FLOW_ID
    AND MDF.TYPE IN ('Process Parameter', 'Flow Parameter')
  ) AS DF_MISSING_COUNT,
  OBW.LAST_MOD_USER,
  OBW.LAST_MOD_DATE
FROM
  STAGE_ONBOARDING_WORKFLOW OBW
LEFT JOIN STAGE_ENTITY P
ON
  OBW.PRODUCER_ID = P.ENTITY_ID
LEFT JOIN STAGE_ENTITY C
ON
  OBW.CONSUMER_ID = C.ENTITY_ID
LEFT JOIN STAGE_ENTITY_COMM PC
ON
  OBW.PRODUCER_COMM_ID = PC.ENTITY_COMM_ID
LEFT JOIN STAGE_ENTITY_COMM CC
ON
  OBW.CONSUMER_COMM_ID = CC.ENTITY_COMM_ID
LEFT JOIN STAGE_DATA_FLOW DF
ON
  OBW.DATA_FLOW_ID = DF.FLOW_ID 
;





